import React, { useState } from "react";

const LoginForm = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    onLogin(username, password); // Passa os dados para a função de login
  };

  return (
    <form
      onSubmit={handleSubmit}
      style={{
        maxWidth: "200px",
        margin: "50px auto",
        padding: "20px",
        border: "1px solid #ddd",
        borderRadius: "10px",
        background: "#f8f8f8",
      }}
    >
      <h2 style={{ textAlign: "center" }}>Login</h2>
      <div style={{ marginBottom: "15px" }}>
        <label style={{ display: "block", marginBottom: "5px" }}>Usuário:</label>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{
            width: "100%",
            padding: "10px",
            borderRadius: "5px",
            border: "1px solid #ddd",
          }}
          required
        />
      </div>
      <div style={{ marginBottom: "15px" }}>
        <label style={{ display: "block", marginBottom: "5px" }}>Senha:</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{
            width: "100%",
            padding: "10px",
            borderRadius: "5px",
            border: "1px solid #ddd",
          }}
          required
        />
      </div>
      <button
        type="submit"
        style={{
          width: "100%",
          padding: "10px",
          borderRadius: "5px",
          border: "none",
          background: "#4CAF50",
          color: "#fff",
          cursor: "pointer",
        }}
      >
        Entrar
      </button>
    </form>
  );
};

export default LoginForm;
