import React from "react";
import './PokemonList.css';

const PokemonList = ({ pokemons, removePokemon }) => {
  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h2>Cartas Cadastradas</h2>
      <ul style={{ listStyle: "none", padding: 0, display: "flex", flexWrap: "wrap", gap: "20px", justifyContent: "center" }}>
        {pokemons.map((pokemon, index) => (
          <li
            key={index}
            style={{
              border: "1px solid #ddd",
              padding: "15px",
              borderRadius: "5px",
              width: "100%",
              maxWidth: "300px",
              boxSizing: "border-box",
              background: "#f8f8f8",
              textAlign: "center",
            }}
          >
            <img
              src={pokemon.cardImage}
              alt={`Carta do ${pokemon.name}`}
              style={{
                marginBottom: "10px",
                border: "1px solid #ccc",
                borderRadius: "5px",
                background: "#fff",
                padding: "5px",
                width: "100%",
                maxWidth: "250px",
              }}
            />
            <p style={{ margin: "5px 0" }}>Nome: {pokemon.name}</p>
            <p style={{ margin: "5px 0" }}>Tipo: {pokemon.type}</p>
            <p style={{ margin: "5px 0" }}>HP: {pokemon.hp}</p>
            <button
              onClick={() => removePokemon(index)}
              style={{
                marginTop: "10px",
                background: "#ff4d4d",
                color: "#fff",
                border: "none",
                padding: "10px 15px",
                borderRadius: "5px",
                cursor: "pointer",
              }}
            >
              Remover Carta
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default PokemonList;
