import React, { useState } from "react";
import PokemonForm from "./components/PokemonForm";
import PokemonList from "./components/PokemonList";
import LoginForm from "./components/LoginForm";
import "./App.css";

const App = () => {return (
  <>
    <h1>Cadastro de Cartas Pokémon</h1>
    <PokemonForm addPokemon={addPokemon} />
    <PokemonList pokemons={pokemons} removePokemon={removePokemon} />
  </>
);

  const [pokemons, setPokemons] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Estado para verificar se o usuário está logado

  const addPokemon = (pokemon) => {
    setPokemons([...pokemons, pokemon]);
  };

  const removePokemon = (indexToRemove) => {
    setPokemons(pokemons.filter((_, index) => index !== indexToRemove));
  };

  const handleLogin = (username, password) => {
    // Validação simples de usuário
    if (username === "admin" && password === "1234") {
      setIsLoggedIn(true);
    } else {
      alert("Usuário ou senha inválidos!");
    }
  };

  return (
    <div className="App">
      {isLoggedIn ? (
        <>
          <h1>Cadastro de Cartas Pokémon</h1>
          <PokemonForm addPokemon={addPokemon} />
          <PokemonList pokemons={pokemons} removePokemon={removePokemon} />
        </>
      ) : (
        <LoginForm onLogin={handleLogin} />
      )}
    </div>
  );
};

export default App;
